import React, { Component } from 'react';
import { connect } from 'react-redux';
import { signIn, signOut } from '../actions/index';

class GoogleAuth extends Component {

    state = { isSignedIn: null };

    componentDidMount() {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '622155317378-qas2cmni5h62kg3c4hfq70c1lrr6cd3u.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({ isSignedIn: this.auth.isSignedIn.get() });
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    };

    onAuthChange = () => {
        this.setState({ isSignedIn: this.auth.isSignedIn.get() });
    };

    renderAuthButton() {
        if (this.state.isSignedIn === null) {
            return null;
        } else if (this.state.isSignedIn) {
            return (
                <button className='ui red google button' onClick={() => this.auth.signOut()}>
                    <i className='google icon'></i>
                    Sign Out
                </button>
            )
        } else {
            return (
                <button className='ui red google button' onClick={() => this.auth.signIn()}>
                    <i className='google icon'></i>
                    Sign In with Google
                </button>
            )
        }
    };

    render() {
        return (
            <div>
                {this.renderAuthButton()}
            </div>
        );
    };
};



export default connect(null, { signIn, signOut })(GoogleAuth);
