import React from 'react';
import { connect } from 'react-redux';
import { deleteStream, fetchStream } from '../../actions/index';
import Modal from '../Modal'
import history from '../../history';
import { Link } from 'react-router-dom';

class StreamDelete extends React.Component {

    componentDidMount() {
        console.log(this.props)
        this.props.fetchStream(this.props.match.params.id)
    }

    renderActions = () => {
        const { id } = this.props.match.params;
        return (
            <React.Fragment>
                <button onClick={() => this.props.deleteStream(this.props.stream.id)} className='ui button negative'>Delete</button>
                <Link to="/" className='ui button'>Cancel</Link>
            </React.Fragment>
        )
    }

    renderContent = () => {
        if (!this.props.stream) {
            return "Are you sure you want to delte this stream?"
        }
        return `Are you sure you want to delte this stream : ${this.props.stream.title}`
    };

    render() {

        return (
            <Modal
                title="Delete Stream"
                content={this.renderContent()}
                actions={this.renderActions()}
                onDismiss={() => history.push("/")}
            />
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return { stream: state.streams[ownProps.match.params.id] }
}

export default connect(
    mapStateToProps,
    { fetchStream, deleteStream }
)(StreamDelete);
