import React, { Component } from 'react';
import { connect } from 'react-redux';
import { SignIn, SignOut } from '../actions/index';

class GoogleAuth extends Component {

    componentDidMount() {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '622155317378-qas2cmni5h62kg3c4hfq70c1lrr6cd3u.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.onAuthChange(this.auth.isSignedIn.get());
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    };

    onAuthChange = (isSignedIn) => {
        if (isSignedIn) {
            this.props.signIn();
        } else {
            this.props.signOut();
        }
    };

    renderAuthButton() {
        if (this.props.isSignedIn === null) {
            return null;
        } else if (this.props.isSignedIn) {
            return (
                <button className='ui red google button' onClick={() => this.props.SignIn()}>
                    <i className='google icon'></i>
                    Sign Out
                </button>
            )
        } else {
            return (
                <button className='ui red google button' onClick={() => this.props.SignOut()}>
                    <i className='google icon'></i>
                    Sign In with Google
                </button>
            )
        }
    };

    render() {
        return (
            <div>
                {this.renderAuthButton()}
            </div>
        );
    };
};

const mapStateToProps = (state) => {
    console.log(state);
    return { isSignedIn: state.auth.isSignedIn }
}

export default connect(mapStateToProps, { SignIn, SignOut })(GoogleAuth);
